import React, { Component } from "react";

import axios from "axios";

export default class User extends Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [],
    };
    this.loadUsers = this.loadUsers.bind(this);
  }

  componentWillMount() {
    this.loadUsers();
  }

  async loadUsers() {
    const promise = await axios.get("http://localhost:3000/user");
    const status = promise.status;
    if (status === 200) {
      const data = promise.data;
      this.setState({ users: data });
    }
  }

  render() {
    return (
      <div>
        <h1>Users</h1>
        {this.state.users.map((users) => (
          <div key={users.id}>
            <table>
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Name</th>
                  <th>Role</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{users.id}</td>
                  <td>{users.name}</td>
                  <td>{users.role}</td>
                </tr>
              </tbody>
            </table>
          </div>
        ))}
        <hr />
        <div>
          <h2>Add USer</h2>
          <form id="userForm">
            <label>Id : </label>
            <input name="id" id="id"></input>
            <br></br>
            <br></br>
            <label>Name : </label>
            <input name="name" id="name"></input>
            <br></br>
            <br></br>
            <label>Role : </label>
            <input name="role" id="role"></input>
            <br></br>
            <br></br>

            <button type="submit">Add User</button>
          </form>
          <hr />
        </div>
      </div>
    );
  }
}
