import React, { Component } from "react";

import axios from "axios";

export default class WishList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      wishList: [],
    };
    this.loadwishList = this.loadwishList.bind(this);
  }

  componentWillMount() {
    this.loadwishList();
  }

  async loadwishList() {
    const promise = await axios.get("http://localhost:3000/wishList");
    const status = promise.status;
    if (status === 200) {
      const data = promise.data;
      this.setState({ wishList: data });
    }
  }

  render() {
    return (
      <div>
        <h1>Wish List</h1>
        {this.state.inventories.map((inventories) => (
          <div key={inventories.id}>
            <tr>
              <td>{inventories.id}</td>
              <td>{inventories.name}</td>
              <td>{inventories.price}</td>
            </tr>
          </div>
        ))}
      </div>
    );
  }
}
